INSERT INTO `issue_basic` VALUES ('835b2d7affb74566853b27663802686d', '02196a21eae0481a9f4ade07cf387957', 'High', 'API Abuse', 'IpUtil.java', 'xxl-job-core/src/main/java/com/xxl/job/core/util/IpUtil.java', 31, '				while (addresses.hasMoreElements()) {\n					address = addresses.nextElement();\n					if (!address.isLoopbackAddress() && address.getHostAddress().indexOf(\":\") == -1) {\n						return address.getHostAddress();\n					}', 'getHostAddress()', '调用 getHostAddress() 而返回的信息不值得信任。攻击者可以欺骗 DNS 条目。勿将 DNS 作为安全性的依据。', '7d909baa69ec42e7893107327197e681', 'Unclassified', 0);
