INSERT INTO `issue_basic` VALUES ('8a2b4560ce504a13aeaa38461ec3eabd', 'cfa89405a53e4b95bc6e44f3741c7199', 'Low', 'Environment', 'web.xml', 'xxl-job-admin/src/main/webapp/WEB-INF/web.xml', 5, '	xmlns=\"http://java.sun.com/xml/ns/javaee\" xmlns:web=\"http://java.sun.com/xml/ns/javaee/web-app_2_5.xsd\"\n	xsi:schemaLocation=\"http://java.sun.com/xml/ns/javaee http://java.sun.com/xml/ns/javaee/web-app_2_5.xsd\"\n	id=\"WebApp_ID\" version=\"2.5\">\n	<display-name>xxl-job-admin</display-name>\n	<context-param>', 'null()', '如果会话超时时间过长，攻击者就会有更多时间危害用户帐户。', '7d909baa69ec42e7893107327197e681', 'Unclassified', 0);
