INSERT INTO `issue_basic` VALUES ('de5b0edc91ab479884a42f29989da25e', 'd612c54ce10d437b9e3a0bffc5be37c6', 'Low', 'Encapsulation', 'common.1.js', 'xxl-job-admin/src/main/webapp/static/js/common.1.js', 21, '	$(\"#logoutBtn\").click(function(){\n		ComConfirm.show(\"确认注销登录?\", function(){\n			$.post(base_url + \"/logout\", function(data, status) {\n				if (data.code == \"200\") {\n					ComAlert.show(1, \"注销成功\", function(){', 'FunctionPointerCall: post()', 'common.1.js 第 21 行中的 HTTP 请求必须包含用户特有的机密，防止攻击者发出未经授权的请求。', '2352e27f5ec942af9b1b27391a3881bd', 'True', 0);
